export default function Concepts(props) {
  const listitems = props.map((item) => (
    <li className="concept">
      <img src={props.image} alt="TODO: TITLE" />
      <h2>{props.title}</h2>
      <p>{props.description}</p>
    </li>
  ));

  return (
    <div className="items">
      <ul id="concepts">{listitems}</ul>
    </div>
  );
}
